    const AddToCart = (item) => {
        setCartItems(prev => [...prev, item])
    }

    // Remove From Cart
    const removeFromCart = (id) => {
        const cart = cartItems.filter((e => e.id !== id))
    }
    return (
        <CartContext.Provider>
            {Children}
        </CartContext.Provider>
