import React, { useState } from 'react'
import './Book-slider.css'
export const Book_slider = ({ data }) => {
    const [bookslide, setBookslide] = useState(0);
    const handleBookSlide = (direction) => {
        if (direction === 'right') {
            setBookslide(bookslide + 1);

        } else {
            setBookslide(bookslide - 1)
        }
    }
    return (
        <div className='book-slider-container'>
            <i onClick={()=>handleBookSlide("left")} className="bi bi-chevron-left  book-slider-arrow-left"></i>
            <div style={{ transform: `translatex(${bookslide * -340}px)` }} className="book-slider-wrapper">
                {
                    data.map((book, k) => {
                        return <div key={k} className="book-slider-item">
                            <img src={`/books/${book.image}`} className='book-slider-item-img' />
                            <h5 className='book-slider-item-title'> {book.title}</h5>
                            <div className="book-slider-item-rating">rating</div>
                            <div className="book-slider-item-price">{book.price}$</div>
                            <div className="book-slider-item-icon-wrapper">
                                <i className="bi bi-eye-fill"></i>
                                <i className="bi bi-cart-plus"></i>
                            </div>
                        </div>
                    })
                }
            </div>
            <i  onClick={()=>handleBookSlide("right")} className="bi bi-chevron-right book-slider-arrow-right"></i>

        </div>
    )
}
