import React, { useEffect, useRef } from 'react';
// const myReff = useRef(null)


export const Navbar = (toggle) => {
    const handleToggle = () => {
        toggle = !toggle;
        setToggle(toggle);
        if (toggle==true) {
            myReff.current.style='left:0px'
        } else{
            myReff.current.style='left:-200px' 
        }


    }

    return (
        <nav ref={myReff}  className="navbar">
            <ul className="nav-links">
                <li className="nav-link">Home</li>
                <li className="nav-link">Authers</li>
                <li className="nav-link">About Us</li>
                <li className="nav-link">Contact Us</li>
                <li className="nav-link">Register</li>
            </ul>
        </nav>
    )
}
